const q = (msg.req.query && msg.req.query.q) ? msg.req.query.q.trim() : '';
const group = (msg.req.query && msg.req.query.group) ? msg.req.query.group.trim() : '';

let sql = "SELECT id, name, price, group_id, sold_count, COALESCE(description,'') as description FROM products";
let where = [];
if(group){
  const gid = parseInt(group);
  if(!isNaN(gid)) where.push("group_id = " + gid);
}
if(q){
  const safe = q.replace(/'/g,"''");
  where.push("(name LIKE '%"+safe+"%' OR COALESCE(description,'') LIKE '%"+safe+"%')");
}
if(where.length) sql += " WHERE " + where.join(" AND ");
sql += " ORDER BY sold_count DESC, id DESC";
msg.topic = sql;
return msg;

// expects JSON body: { username, password_hash }
const body = msg.req.body || msg.payload || {};
const u = (body.username || '').replace(/'/g, "''");
msg._password_hash = body.password_hash || '';
msg.topic = `SELECT id, username, password_hash, is_admin FROM users WHERE username='${u}' LIMIT 1`;
return msg;

const rows = msg.payload || [];
const supplied = msg._password_hash || (msg.req.body && msg.req.body.password_hash) || '';

if (!rows || rows.length === 0) {
  msg.statusCode = 401;
  msg.payload = { success:false, message:'Invalid username or password' };
  return msg;
}

const user = rows[0];
if (user.password_hash !== supplied) {
  msg.statusCode = 401;
  msg.payload = { success:false, message:'Invalid username or password' };
  return msg;
}

// Lưu user để dùng ở bước sau
msg._user = { id: user.id, username: user.username, is_admin: user.is_admin };

// gửi xuống node UUID để sinh token
return msg;

const token = msg.payload; // token từ UUID node
const user = msg._user;

// lưu token lại để func4 dùng
msg._token = token;

// tạo câu lệnh SQL insert
msg.topic = `INSERT INTO sessions (token, user_id) VALUES ('${token}', ${user.id})`;

// payload cho node mysql
msg.payload = {};
return msg;

// lấy token từ header hoặc cookie
let token = '';
if (msg.req && msg.req.headers['x-session-token']) {
    token = msg.req.headers['x-session-token'];
} else if (msg.req && msg.req.cookies && msg.req.cookies.session_token) {
    token = msg.req.cookies.session_token;
}

// nếu không có token
if (!token) {
    msg.statusCode = 401;
    msg.payload = { success: false, message: 'No token provided' };
    return msg;
}

// tránh SQL injection
const safeToken = token.replace(/'/g, "''");

// tạo câu truy vấn
msg.topic = `
  SELECT s.token, s.user_id, u.username, u.is_admin
  FROM sessions s
  JOIN users u ON s.user_id = u.id
  WHERE s.token='${safeToken}'
  LIMIT 1
`;

return msg;

const rows = msg.payload || [];

if (!rows || rows.length === 0) {
    msg.statusCode = 401;
    msg.payload = { success: false, message: 'Invalid or expired session' };
    return msg;
}

const r = rows[0];
msg.statusCode = 200;
msg.payload = {
    success: true,
    user: {
        id: r.user_id,
        username: r.username,
        is_admin: !!r.is_admin
    }
};
return msg;

// lấy token
let token = msg.req?.headers['x-session-token'] || '';
if (!token) {
  msg.statusCode = 401;
  msg.payload = { success: false, message: 'No session token' };
  return msg;
}
const safeToken = token.replace(/'/g, "''");

// truy vấn để kiểm tra quyền admin
msg.topic = `
  SELECT u.id, u.username, u.is_admin
  FROM sessions s
  JOIN users u ON s.user_id = u.id
  WHERE s.token='${safeToken}'
  LIMIT 1
`;
return msg;

const rows = msg.payload || [];
if (!rows || rows.length === 0) {
  msg.statusCode = 401;
  msg.payload = { success: false, message: 'Invalid session' };
  return msg;
}
const user = rows[0];
if (!user.is_admin) {
  msg.statusCode = 403;
  msg.payload = { success: false, message: 'Access denied' };
  return msg;
}

// truy vấn toàn bộ đơn hàng
msg.topic = `
  SELECT o.id AS order_id, o.customer_name, o.phone, o.address, o.total,
         o.status, o.created_at,
         oi.product_id, oi.qty, oi.price,
         p.name AS product_name
  FROM orders o
  LEFT JOIN order_items oi ON oi.order_id = o.id
  LEFT JOIN products p ON p.id = oi.product_id
  ORDER BY o.created_at DESC, o.id DESC
`;
return msg;

// func4: set cookie + trả response
const token = msg._token || msg.token || msg.payload.token;
const user = msg._user;

// nếu có đối tượng response (Node-RED >= 3.1)
if (msg.res && msg.res.cookie) {
    msg.res.cookie('session_token', token, {
        httpOnly: false,  // cho phép JS đọc cookie
        path: '/',
        maxAge: 3 * 24 * 60 * 60 * 1000 // 3 ngày
    });
} else {
    // fallback: dùng header Set-Cookie cho Node-RED cũ
    msg.headers = {
        'Set-Cookie': `session_token=${token}; Path=/; Max-Age=${3 * 24 * 60 * 60}`
    };
}

// gửi payload JSON cho client
msg.statusCode = 200;
msg.payload = {
    success: true,
    token: token,
    user: user
};
return msg;

const rows = msg.payload || [];
if (!rows || rows.length === 0) {
    msg.payload = { success: true, orders: [] };
    return msg;
}

const orders = {};
rows.forEach(r => {
    if (!orders[r.order_id]) {
        orders[r.order_id] = {
            id: r.order_id,
            customer_name: r.customer_name,
            phone: r.phone,
            address: r.address,
            total: r.total,
            status: r.status,
            created_at: r.created_at,
            items: []
        };
    }
    orders[r.order_id].items.push({
        product_id: r.product_id,
        product_name: r.product_name,
        qty: r.qty,
        price: r.price
    });
});

msg.payload = { success: true, orders: Object.values(orders) };
return msg;

const order = msg.req.body || {};
if (!order.items || !Array.isArray(order.items) || order.items.length === 0) {
  msg.statusCode = 400;
  msg.payload = { success: false, message: 'Không có sản phẩm trong đơn hàng' };
  return msg;
}

// Lấy token từ header
let token = msg.req.headers['x-session-token'] || '';
if (!token) {
  msg.statusCode = 401;
  msg.payload = { success: false, message: 'Thiếu token xác thực' };
  return msg;
}
token = token.replace(/'/g, "''");

// Query kiểm tra user theo token
msg.topic = `
  SELECT u.id, u.username, u.is_admin
  FROM sessions s 
  JOIN users u ON s.user_id = u.id 
  WHERE s.token='${token}' 
  LIMIT 1
`;

msg._order = order;
return msg;

const rows = msg.payload || [];
if (!rows || rows.length === 0) {
    msg.statusCode = 401;
    msg.payload = { success: false, message: 'Token không hợp lệ hoặc hết hạn' };
    return msg;
}

const user = rows[0];
const order = msg._order;

const name = (order.customer_name || '').replace(/'/g, "''");
const phone = (order.phone || '').replace(/'/g, "''");
const addr = (order.address || '').replace(/'/g, "''");

// Tính tổng tiền
const total = order.items.reduce((sum, i) => sum + (i.price * i.qty), 0);

msg.topic = `
  INSERT INTO orders (user_id, customer_name, phone, address, total)
  VALUES (${user.id}, '${name}', '${phone}', '${addr}', ${total})
`;

msg._user = user;
msg._order = order;
return msg;

msg.statusCode = 200;
msg.payload = {
    success: true,
    order_id: msg._order_id,
    message: 'Đặt hàng thành công!'
};
return msg;

