  JOIN users u ON s.user_id = u.id
  WHERE s.token='${safeToken}'
  LIMIT 1
`;
return msg;

const rows = msg.payload || [];
if (!rows || rows.length === 0) {
  msg.statusCode = 401;
  msg.payload = { success:false, message:'Invalid session' };
  return msg;
}

const user = rows[0];
if (user.is_admin) {
  msg.statusCode = 403;
  msg.payload = { success:false, message:'Access denied for admin' };
  return msg;
}

