  SELECT o.id AS order_id, o.customer_name, o.phone, o.address, o.total,
         o.status, o.created_at,
         oi.product_id, oi.qty, oi.price,
         p.name AS product_name
  FROM orders o
  LEFT JOIN order_items oi ON oi.order_id = o.id
  LEFT JOIN products p ON p.id = oi.product_id
  WHERE o.user_id = ${user.user_id}
  ORDER BY o.created_at DESC, o.id DESC
`;
return msg;

const rows = msg.payload || [];
if (!rows || rows.length === 0) {
    msg.payload = { success: true, orders: [] };
    return msg;
}

const orders = {};
rows.forEach(r => {
    if (!orders[r.order_id]) {
        orders[r.order_id] = {
            id: r.order_id,
            customer_name: r.customer_name,
            phone: r.phone,
            address: r.address,
            total: r.total,
            status: r.status,
            created_at: r.created_at,
            items: []
        };
    }
    orders[r.order_id].items.push({
        product_id: r.product_id,
        product_name: r.product_name,
        qty: r.qty,
        price: r.price
    });
});

msg.payload = { success: true, orders: Object.values(orders) };
return msg;

const insertResult = msg.payload;
const orderId = insertResult.insertId;
const order = msg._order;

if (!orderId) {
    msg.statusCode = 500;
    msg.payload = { success: false, message: 'Không tạo được đơn hàng' };
    return msg;
}

// Sinh query multi insert cho order_items
const values = order.items.map(i =>
  `(${orderId}, ${i.product_id}, ${i.qty}, ${i.price})`
).join(',');

msg.topic = `
  INSERT INTO order_items (order_id, product_id, qty, price)
  VALUES ${values}
`;

msg._order_id = orderId;
return msg;
